import React from "react";

const Home = () => {
  return (
    <>
      <div className="hero border-1 pb-3">
        <div className="card bg-dark text-white border-0 mx-3">
          <img
            className="card-img img-fluid"
            src="./assets/main.png.jpg"
            alt="Card"
            height={500} // Keeps the original image size intact
          />
          <div className="card-img-overlay d-flex align-items-center">
            <div className="container">
              <h5
                className="card-title fs-1 text fw-bold"
                style={{
                  display: "inline-block", // Limits the background to text width
                  padding: "10px 20px",
                  borderRadius: "5px",
                }}
              >
                Seasonal Harvest
              </h5>
              <p
                className="card-text fs-5 d-none d-sm-block"
                style={{
                  display: "inline-block", // Ensures the background wraps the text content only
                  padding: "10px 20px",
                  borderRadius: "5px",
                  marginTop: "10px",
                  maxWidth: "600px", // Restricts the width of the paragraph to a readable size
                }}
              >
                From Farm üë©üèª‚Äçüåæ to your Table üß∫ Fresh and Organic Produce!
              </p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
